package com.lary.health.ui.widget;import android.content.Context;import android.support.v4.view.ViewPager;import android.util.AttributeSet;import android.view.MotionEvent;/** * @author zhaoyapeng * @version create time:${date}${time} * @Email zhaoyp@witmob.com * @Description $  为了兼容首页，使用viewpager 管理首页 */public class HomeViewPager extends ViewPager {    private boolean notTouchScoll = false;    public HomeViewPager(Context context, AttributeSet attrs) {        super(context, attrs);    }    public HomeViewPager(Context context) {        super(context);    }    public void setNotTouchScoll(boolean scoll) {        this.notTouchScoll = scoll;    }    @Override    public boolean onTouchEvent(MotionEvent ev) {        if (notTouchScoll) {            return false;        } else {            return super.onTouchEvent(ev);        }    }    @Override    public boolean onInterceptTouchEvent(MotionEvent ev) {        if (notTouchScoll) {            return false;        } else {            return super.onInterceptTouchEvent(ev);        }    }}